function pageprocessorSignIn279(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  $scope.byPassLogin = function() {
    $rootScope.loggedIn = true;
    $location.path("/app/WhatsUp/WhatsUp");
  }

  $rootScope.back = function() {
    $rootScope.leftToRight = true;
    $location.path("/app/Menu82/Menu82");
    //window.location.href = "index.html#app/Menu82/Menu82";
  }

  $scope.login = function() {
    if ($("#loginUsername").val().length == 0) {
      navigator.notification.alert('Please enter your username.');
      return false;
    }
    if ($("#loginPassword").val().length == 0) {
      navigator.notification.alert('Please enter your password.');
      return false;
    }
    var username = $("#loginUsername").val();
    var password = $("#loginPassword").val();
    $rootScope.loggedin == false;
    window['authFunction'](username, password, $http, $rootScope, function(data) {
      if (data.error) {
        $rootScope.ticket = null;
        $rootScope.loggedin = false;
        $rootScope.userroles = null;
        $.jStorage.deleteKey('username');
        $.jStorage.deleteKey('password');
        $.jStorage.deleteKey('ticket');
        $.jStorage.deleteKey('userroles');

      } else {
        $rootScope.loggedIn = true;
        var ticket = data.ticket;
        $rootScope.ticket = ticket;
        $rootScope.userroles = data.authdata.role;
        $.jStorage.set('username', username);
        $.jStorage.set('password', password);
        $.jStorage.set('ticket', ticket);
        $.jStorage.set('userroles', $rootScope.userroles);
        if ($rootScope.loggedIn == true && $rootScope.from != null) {
          var from = $rootScope.from;
          $rootScope.from = null;
          $location.path("/app/" + from + "/" + from);
        } else {
          //$rootScope.loggedIn = true;
          $location.path("/home");
        }
        $rootScope.$apply();
      }
    }, function(data) {
      apprise("Error occurred while processing this request.", {
        'verify': false,
        'textYes': "Ok"
      }, function(r) {
        $rootScope.ticket = null;
        $rootScope.loggedin = false;
        $rootScope.userroles = null;
        $.jStorage.deleteKey('username');
        $.jStorage.deleteKey('password');
        $.jStorage.deleteKey('ticket');
        $.jStorage.deleteKey('userroles');
      });
    });

  }

}

