        page += 1;
        if (window.device) {
            var feedUrl = "/unifydwhatsup/feed?size=10&category=" + $rootScope.user.areaOfInterest.toString() + "&sort=updatedDate,DESC&page=" + page;
        } else {
            var feedUrl = "/unifydwhatsup/feed?platform=webapp&size=10&category=" + $rootScope.user.areaOfInterest.toString() + "&sort=updatedDate,DESC&page=" + page;
        }
        $rootScope.callAPI(feedUrl, 'GET', '', function(data) {
            var data = data.data;
            if (data.last) {
                $scope.hideLoadMore = false;
                page = 0;
            }
            for (var i = 0; i < data.content.length; i++) {
                data.content[i].limitComment = -3;
                data.content[i].showViewAllComment = true;
                $scope.posts.push(data.content[i]);
