        return $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0");
    };

    $scope.trusthtml = function(data) {
        return $sce.trustAsHtml(data);
    }
    $scope.loadYTFrame = function($event) {


        var elem = $event.currentTarget;
        var newEle = angular.element("<iframe frameborder='0'></iframe>");
        var embed = "https://www.youtube.com/embed/ID";
        newEle.attr("src", embed.replace("ID", angular.element($event.currentTarget).attr('data-id')));
        var e = $compile($(newEle))($scope);
        $(elem).replaceWith($(e));
    }
}

function pageprocessorWhatsUpPage3(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    try {

        $scope.p = $rootScope.postDetails;
        $scope.areaOfInterest = $rootScope.areaOfInterest;
        $scope.getCategory = function(arr) {
            var postCategory = [];
            for (var i = 0; i < $scope.areaOfInterest.length; i++) {
                for (var j = 0; j < arr.length; j++) {
                    if ($scope.areaOfInterest[i]['id'] == arr[j]) {
                        postCategory.push($scope.areaOfInterest[i]['category']);
                    }
                }
            }
            if (postCategory.length == 0) {
                return "";
            } else {
                return postCategory.toString().replace(/,/g, ', ');
            }

        }
        var data2 = '';
        data2 = '<div id="commentMainBox" class="list-group-item listitemborderTopSolid listitemborderSolid rounded-0" style="bottom: 40px;position: fixed;width: 100%;height:53px;"><div class="col-sm-10 col-xs-10 colPadding" style="padding-left:0px;"><input id="commentBox" ng-change="commentChange()" type="text" ng-focus="commentBoxFocus();" ng-blur="commentBoxFocusOut();" ng-model="comment" placeholder="Add a comment" maxlength="140" style="border:none;outline:none;color:#000;padding:3px 0px 3px 0px;width:99%;font-size:14px;" /></div><div class="col-sm-2 col-xs-2 colPadding" style="padding-right:0px;"><div class="replybtn proximaSemiBoldFont fontSize14 replybtnText" style="margin-top:9px;text-align:right;display:block;" ng-click="postComment();">REPLY</div></div><div style="clear:both"></div></div>';
        var htmlView = $compile($(data2))($scope);
        $("#fixedFooterContent").html(htmlView);

        var footerHeight = $('#footerContentContainer').height();
        $("#commentMainBox").css({'bottom':footerHeight+'px'});
        

        var likesUrl = "/unifydwhatsup/like/" + $rootScope.postDetails.id;
        $rootScope.callAPI(likesUrl, 'GET', '', function(data) {
            //alert(JSON.stringify(data.data.content));
            $scope.p.like = data.data.content;
            $scope.hideLikesLoader = false;

            var commentUrl = "/unifydwhatsup/comment/" + $rootScope.postDetails.id + "?size=3&sort=createdDate,DESC";
            $rootScope.callAPI(commentUrl, 'GET', '', function(data) {
                $scope.p.comments = data.data.content.reverse();
                //$scope.p.limitComment = data.data.content.length;
                var data1 = $compile($(pagedef.datatemplate))($scope);
                $("#appContent").append(data1);

                if ($rootScope.fromApp == "comment") {
                    setTimeout(function() {
                        $scope.commentBoxFocus();

                    }, 500);
                    setTimeout(function() {
                        $("#commentBox").focus();
                    }, 800);


                    $rootScope.fromApp = "";
                }

                $scope.$apply();
            });
        });

        $scope.showAllComments = function(id) {
            var commentUrl = "/unifydwhatsup/comment/" + $rootScope.postDetails.id;
            $rootScope.callAPI(commentUrl, 'GET', '', function(data) {
                $scope.p.comment = data.data.content.length;
                $scope.p.comments = data.data.content;
                $scope.p.limitComment = data.data.content.length;
                $scope.p.showViewAllComment = false;
                //$scope.$apply();
            });
        }


        $scope.likeUnlike = function(id, index) {
            /*$('#likeUnlike'+index).addClass('pulse animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {
                $(this).removeClass('pulse animated');
            });*/

            if ($scope.p.liked) {

                var feedUrl = "/unifydwhatsup/like/" + $scope.p.id;
                $rootScope.callAPI(feedUrl, 'DELETE', '', function(data) {
                    var likeIndex = $scope.p.like.findIndex(function(x) {
                        return x.id == id
                    });
                    $scope.p.like.splice(likeIndex, 1);
                    $scope.p.liked = false;
                    $scope.p.likes -= 1;
                });
            } else {
                var feedUrl = "/unifydwhatsup/like";
                var data = {
                    "postId": $scope.p.id,
                    "username": $rootScope.user.firstName + " " + $rootScope.user.lastName,
                    "userimage": $rootScope.user.profileImage,
                    "userrole": $rootScope.user.role
                };
                $rootScope.callAPI(feedUrl, 'POST', data, function(data) {
                    $scope.p.like.push(data.data);
                    $scope.p.liked = true;
                    $scope.p.likes += 1;
                });

            }
        }

        $scope.commentBoxFocus = function() {
            $("#footerContentContainer").removeClass('footerGroupIcons').addClass('hideFooter');
            //$(".bottomPoweredBy").css('display', 'none !important');

            $("#bottomPoweredBy").addClass('hideFooter');

            $("#commentMainBox").css('bottom', '0px');

            //$('.scrollable-content').css('padding-bottom',btmPadding-btm+'px');

            setTimeout(function() {
                $('.scrollable-content').animate({
                    scrollTop: $('#appContent').height() + 1000
                }, 'slow');
            }, 50);
            return false;
        }
        $scope.commentBoxFocusOut = function() {
            $("#footerContentContainer").removeClass('hideFooter').addClass('footerGroupIcons');
            //$(".bottomPoweredBy").css('display', 'block !important');
            $("#bottomPoweredBy").removeClass('hideFooter');
            $("#commentMainBox").css('bottom', footerHeight+'px');
            //$("#commentMainBox").css('bottom',btm+'px');

            //$('.scrollable-content').css('padding-bottom',btmPadding+'px');
        }

        $scope.$on("$destroy", function() {
            //$("#commentMainBox").html("");
        });

        $scope.commentChange = function() {
            var l = document.getElementById("commentBox").value.length;
            if (l == 0) {
                $(".replybtn").removeClass('yellowText').addClass('replybtnText');
            } else {
                $(".replybtn").removeClass('replybtnText').addClass('yellowText');
            }
        }

        $scope.postComment = function() {
            if ($scope.comment) {
                var feedUrl = "/unifydwhatsup/comment";
                var data = {
                    "postId": $scope.p.id,
                    "username": $rootScope.user.firstName + " " + $rootScope.user.lastName,
                    "userimage": $rootScope.user.profileImage,
                    "userRole": $rootScope.user.role,
                    "comment": $scope.comment
                }
                $rootScope.callAPI(feedUrl, 'POST', data, function(data) {
                    if (!data.data.error) {
                        $scope.p.comment += 1;
                        $scope.p.comments.push(data.data);
                        if (!$scope.p.showViewAllComment) {
                            $scope.p.limitComment = $scope.p.comment;
                        }
                        $scope.comment = "";
                        $scope.$apply();
                        $(".replybtn").removeClass('yellowText').addClass('replybtnText');
                        $(".footerGroupIcons").css('display', 'block !important');
                        $("#bottomPoweredBy").removeClass('hideFooter');
                        $('.scrollable-content').animate({
                            scrollTop: $('#appContent').height() + 1000
                        }, 'slow');

                    }
                });
            } else {
                if (window.device) {
                    navigator.notification.alert('Please add comment.', null, 'Comment', 'Ok')
                } else {
                    alert('Please add comment.');
                }
            }
        }

        $scope.addComment = function() {
            $("#footerContentContainer").removeClass('footerGroupIcons').addClass('hideFooter');
            $("#bottomPoweredBy").addClass('hideFooter');
            $("#commentMainBox").css('bottom', '0px');

            //$('.scrollable-content').css('padding-bottom',btmPadding-btm+'px');

            $("#commentBox").focus();
            setTimeout(function() {
                $('.scrollable-content').animate({
                    scrollTop: $('#appContent').height() + 1000
                }, 'slow');
            }, 50)
            return false;
        }

        $scope.showPostLikes = function() {

            $rootScope.postLikes = $scope.p.like;
            $location.path("/app/WhatsUp/WhatsUpPage4");
        }


        $scope.likeComment = function(id) {
            if ($scope.comments[id].commentLiked) {
                $scope.comments[id].commentLiked = false;
            } else {
                $scope.comments[id].commentLiked = true;
            }
        }

        $scope.delComment = function(id) {
            var DeleteCommentIndex = $scope.p.comments.findIndex(function(x) {
                return x.id == id
            });

            var userdelPost = function(btn) {
                if (btn == 1) {
                    var postUrl = "/unifydwhatsup/comment/" + id;
                    $rootScope.callAPI(postUrl, 'DELETE', '', function(data) {
                        /* Add condition here to check successful deletion. By passing it as notification service is down */
                        $scope.p.comments.splice(DeleteCommentIndex, 1);
                        $scope.p.comment -= 1;
                    });
                }
            }


            if ($rootScope.webApp) {
                userdelPost(1);
            } else {
                var options = {
                    'androidTheme': window.plugins.actionsheet.ANDROID_THEMES.THEME_HOLO_LIGHT,
                    'title': '',
                    'buttonLabels': ['Delete Comment', 'Cancel']
                };
                window.plugins.actionsheet.show(options, userdelPost);
            }
        }
        $scope.openPostVideo = function(e, id) {
            e.stopPropagation();
            window.open('https://www.youtube.com/watch?v=' + id, '_blank');
        }

    } catch (e) {}

    $scope.trustSrc = function(videoID) {
        return $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0");
      };
}

