        $scope.showTagsList = true;
        $scope.showAddTag = false;
        /*$('#CategoryList').addClass('slideInDown animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {
         $(this).removeClass('pulse animated');
         });*/
