    if ($scope.textname != "" || $scope.textname == undefined) {
      var nameContent = $scope.textname;
    } else {
      var nameContent = "Name"
      navigator.notification.alert("Please fill up the Name",
        null, "Alert", "OK")
      return;
    }
    if ($scope.textemail != "" || $scope.textname == undefined) {
      var emailContent = $scope.textemail;
    } else {
