      navigator.notification.alert(
        "Please add your email address", null, "Alert",
        "OK")
      return;
    }

    if ($scope.textprayer != "") {
      var prayerContent = $scope.textprayer;
    } else {
      var prayerContent = "Prayer request"
      navigator.notification.alert("Please fill up the Feedback",
        null, "Alert", "OK")
      return;
    }

    $.blockUI({
      message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
    });
    emailBody = "";
    emailBody = "<html><body>";
    emailBody += "<div>Details: <br>Name: " + nameContent +
      "<br>Email: " + emailContent + "</div><br/>";
    emailBody += "<div>" + prayerContent + "</div><br/>";
    emailBody += "</body></html>";
    var emailData = {
      'to': 'help@its.utexas.edu',
      'subject': 'Feedback reported for UT Austin Mobile App',
      'body': emailBody,
      'from': 'help@its.utexas.edu'
    };
    //alert(JSON.stringify(emailData));

    var xhr = $.ajax({
      url: "https://kryptos.kryptosmobile.com/gateway/CEAI/kryptosemailsender",
      contentType: 'application/json',
      type: "POST",
      headers: {
        'licenseKey': 'UjpIaWVDVzpTOjE0NDk0ODk2MzUxMzE6VTpha0BjYW1wdXNlYWkub3JnOlQ6NDpQOjI4Ng=='
      },
      data: JSON.stringify(emailData),
      success: function(data) {
        $.unblockUI();
        //alert(JSON.stringify(data));
        if (data.response == "Successful") {
          $.unblockUI();
          navigator.notification.alert(
            "Your request has been submitted ",
            null, 'Thank you!', 'Ok');
          $scope.textname = "";
          $scope.textemail = "";
          $scope.textprayer = "";
          $scope.$apply();

        }
      },
      error: function(err) {
        alert(JSON.stringify(err));
      }
    });
  }

}

